import React, {Component} from 'react';
import ReactPaginate from 'react-paginate';
import { connect } from "react-redux";
import { fetchInfos } from '../actions/uscensuslearnActions';
import './homepagestyle.css';

class HomePage extends Component {
	constructor(){
		super()
		this.state={
			val:"",
			nb_person: 0, 
			nb_line: 0,
			loading: false,
			currentPage: 1,
			personPerPage: 10
		}
	}
	componentDidMount(){
		
		this.props.fetchInfos()
		
	}
	
	static getDerivedStateFromProps(nextProps, prevState){
		if(nextProps.infos.infos.length!==prevState.nb_person){
		  return { nb_person: nextProps.infos.infos.length};
	   }
	   else return null;
	 }
	 
	 componentDidUpdate(prevProps, prevState) {
	   if(prevProps.infos.infos.length!==this.props.infos.infos.length){
		 //Perform some operation here
		 this.setState({nb_person: this.props.infos.infos.length});
		 this.props.fetchInfos();
	   }
	 }

	paginate(pageNumber){
		this.setState({
			currentPage: pageNumber
		},() => {
		  this.props.fetchInfos();
		});
	}
  

	render(){
		
		const {infos} = this.props.infos;
		const indexOfLastInfo = this.state.currentPage * this.state.logementPerPage;
		const indexOfFirstInfo = indexOfLastInfo - this.state.logementPerPage;
		const currentLogement = logements.slice(indexOfFirstInfo, indexOfLastInfo);
		console.log(currentLogement);
		const renderLogements =currentLogement.map(logement => {

			      if(logement.fk_ville===1){
			          this.state.val="Paris et Ã®le de France"
			      }
			      else if(logement.fk_ville===2){
			          this.state.val="Lyon"
			      }
			      else if(logement.fk_ville===3){
			          this.state.val="Bordeau"
			      }
			      else if(logement.fk_ville===4){
			          this.state.val="Marseille"
			      }
			     this.state.nb_line= this.props.logement.logements.indexOf(logement)+1;
			      return ( <tr key={logement.id}>
			      		  <td> {this.state.nb_line} </td>
			              <td> {logement.id} </td>
			              <td> {logement.type} </td>
			              <td> {logement.adresse} </td>
			              <td> {logement.code_postal} </td>
			              <td> {this.state.val} </td>
			              <td> <a href={`/logements/logement/${logement.id}`} className="btn btn-outline-secondary btn-sm rounded-0"> voir plus</a> </td>
			              
			            </tr>

			        );
    	});


		console.log(infos.length)
		return(
			<div>
				<div style={{marginTop:'1em'}}>

 				<div className="container-fluid">
				    <div className="row"> 
				      <div className="col-md-4 mt-4">
				          <div className="card">
				            <div className="text-white p-4" style={{backgroundColor:'#00cccb'}}>
				              <i className="fas fa-home fa-6x"></i>
				              <h2 className="float-right font-weight-bold" style={{fontSize: '50px', textAlign: 'center'}}>{this.state.nb_person}<span className="d-block">rows</span></h2>
				            </div>
				            
				          </div>
				          <div className="card mt-5">
					            <div className=" text-white p-4 " style={{backgroundColor:'#ed7e24'}}>
		                      <i className="fas fa-list-ul fa-6x"></i>
		                      <h2 className="float-right font-weight-bold" style={{fontSize: '40px', textAlign: 'center'}}>xx <span className="d-block">xxxxxxxxxx</span></h2>
		                    </div>
				            
				          </div>
				          
				      </div> 
				      <div className="col-8 ">
				        <h2 style={{backgroundColor: '#ed7e24'}} className="text-white p-2 ">The data</h2>
				        <div className="container d-flex justify-content-center">
				        
				        <table className="table mt-3">
				          <thead className="table-dark">
				            <tr>
				              <th>#</th>
				              <th>id</th>
				              <th>Type</th>
				              <th>Adresse</th>
				              <th>Code Postal</th>
				              <th>Ville</th>
				              <th>Action</th>
				            </tr>
				          </thead>
				          <tbody>

				          </tbody>
				        </table>
				      </div>
				    </div>
				      
				    </div>
				</div>
				
				{infos.length > this.state.personPerPage ? <ReactPaginate pageCount={Math.ceil(infos.length/this.state.personPerPage)} pageRangeDisplayed={2}  marginPagesDisplayed={2} onPageChange={(data) =>this.paginate(data.selected+1)} containerClassName={"pagination"} previousLabel={"prÃ©cÃ©dent"} nextLabel={"suivant"}/> : ''}
				</div>
			</div>
			)
	}
}

const mapStateToProps=state =>({
	infos: state.infos
 })
 
 export default connect(
   mapStateToProps, {
	 fetchInfos })(HomePage)
