import axios from 'axios';
import { GET_INFOS, INFOS_LOADING} from './types.jsx';


export const fetchInfos = () => (dispatch) => {
axios
	.get('http://127.0.0.1:8000/uscensus/')
	.then(res=> 
		dispatch({
			type: GET_INFOS,
			payload: res.data
		})
		)
	.catch(err=>
		console.log(err));
};
